# Mensajes de salas.
# Lista de mensajes enviados por usuarios de una sala.
#
# Campos:
# Id - Identificador único
# RoomId - Referencia a la sala
# UserId - Referencia al usuario
# Content - Contenido textual del mensaje enviado
# CreatedAt - Fecha y hora de envío del mensaje

DROP TABLE IF EXISTS `TR_RealtimeRoomMessages`;

CREATE TABLE `TR_RealtimeRoomMessages` (
  `Id` VARCHAR(64) NOT NULL,
  `RoomId` VARCHAR(64) NOT NULL,
  `UserId` VARCHAR(10) NOT NULL,
  `Content` TEXT NOT NULL,
  `CreatedAt` DATETIME NOT NULL,
  PRIMARY KEY (`Id`),
  FOREIGN KEY (`RoomId`) REFERENCES TP_RealtimeSpaceRooms(`Id`) ON DELETE CASCADE,
  FOREIGN KEY (`UserId`) REFERENCES TP_Usuarios(`IdUsr`) ON DELETE CASCADE
) ENGINE=MyISAM DEFAULT CHARSET=latin1;
