# Usuarios de salas.
# Datos de usuarios pertenecientes a una sala.
#
# Campos:
# Id - Identificador Ãºnico
# RoomId - Referencia a la sala
# UserId - Referencia al usuario
# Moderator - Si el usuario es moderador de sala
# Inactive - Si el usuario se encuentra inactivo en la sala

DROP TABLE IF EXISTS `TR_RealtimeRoomUsers`;

CREATE TABLE `TR_RealtimeRoomUsers` (
  `Id` VARCHAR(64) NOT NULL,
  `RoomId` VARCHAR(64) NOT NULL,
  `UserId` VARCHAR(10) NOT NULL,
  `Moderator` TINYINT(1) DEFAULT 0,
  `Inactive` TINYINT(1) DEFAULT 0,
  PRIMARY KEY (Id),
  FOREIGN KEY (RoomId) REFERENCES TP_RealtimeSpaceRooms(Id) ON DELETE CASCADE,
  FOREIGN KEY (UserId) REFERENCES TP_Usuarios(IdUsr) ON DELETE CASCADE
) ENGINE=MyISAM DEFAULT CHARSET=latin1;
