# Espacios de comunicación.
# Un espacio es una agrupación de salas de por aplicación. Por ejemplo,
# la escuela en general, el Aula Virtual, PQRS, foros, etc.
#
# Campos:
# Id - Identificador único
# Code - Código único para comunicaciones entre COMA y RealTime
# Name - Nombre de espacio

DROP TABLE IF EXISTS `TB_RealtimeSpaces`;

CREATE TABLE `TB_RealtimeSpaces` (
  `Id` VARCHAR(64) NOT NULL,
  `Code` VARCHAR(32) NOT NULL,
  `Name` VARCHAR(255) DEFAULT NULL,
  PRIMARY KEY (`Id`)
) ENGINE=MyISAM DEFAULT CHARSET=latin1;
