#!/bin/bash

MYSQL_DB="${MYSQL_DB:-diamante_test}"
MYSQL_USER="${MYSQL_USER:-root}"
MYSQL_PASS="${MYSQL_PASS:-admin}"

# El orden es importante
mysql $MYSQL_DB --user=$MYSQL_USER --password=$MYSQL_PASS < ./mocks/users.sql
mysql $MYSQL_DB --user=$MYSQL_USER --password=$MYSQL_PASS < ./mocks/spaces.sql
mysql $MYSQL_DB --user=$MYSQL_USER --password=$MYSQL_PASS < ./mocks/spaces-rooms.sql
mysql $MYSQL_DB --user=$MYSQL_USER --password=$MYSQL_PASS < ./mocks/rooms-messages.sql
mysql $MYSQL_DB --user=$MYSQL_USER --password=$MYSQL_PASS < ./mocks/rooms-users.sql
